                        `type`="number", 
                        `format`="zto"),
                    list(
                        `name`="robustness", 
                        `title`="Robustness", 
                        `type`="text"))))
            self$add(jmvcore::Table$new(
                options=options,
                name="crossValidation",
                title="Cross-Validation Results",
                visible="(cross_validation)",
                clearWith=list(
                    "cross_validation",
                    "cv_folds"),
                columns=list(
                    list(
                        `name`="fold", 
                        `title`="Fold", 
                        `type`="integer"),
                    list(
                        `name`="log_predictive_score", 
                        `title`="Log Predictive Score", 
                        `type`="number", 
                        `format`="zto"),
                    list(
                        `name`="deviance", 
                        `title`="Deviance", 
                        `type`="number", 
                        `format`="zto"),
                    list(
                        `name`="c_index", 
                        `title`="C-Index", 
                        `type`="number", 
                        `format`="zto"),
                    list(
                        `name`="calibration", 
                        `title`="Calibration", 
                        `type`="number", 
                        `format`="zto"))))
            self$add(jmvcore::Image$new(
                options=options,
                name="modelProbsPlot",
                title="Model Posterior Probabilities",
                width=600,
                height=400,
                visible="(plot_model_probs)",
                requiresData=TRUE,
                clearWith=list(
                    "mcmc_iterations",
