                clearWith=list(
                    "pred_vars",
                    "variable_selection_threshold")))
            self$add(jmvcore::Image$new(
                options=options,
                name="coefficientsPlot",
                title="Posterior Coefficient Distributions",
                width=700,
                height=600,
                visible="(plot_coefficients)",
                requiresData=TRUE,
                clearWith=list(
                    "pred_vars",
                    "credible_level")))
            self$add(jmvcore::Image$new(
                options=options,
                name="convergencePlot",
                title="MCMC Convergence Diagnostics",
                width=800,
                height=600,
                visible="(plot_convergence)",
                requiresData=TRUE,
                clearWith=list(
                    "mcmc_chains",
                    "mcmc_iterations")))
            self$add(jmvcore::Image$new(
                options=options,
                name="modelSpacePlot",
