#' Clinical Data Integration
#'
#' 
#' @param data .
#' @param dataSource .
#' @param patientIdVar .
#' @param dateVars .
#' @param clinicalVars .
#' @param qualityCheck .
#' @param completenessThreshold .
#' @param consistencyCheck .
#' @param outlierDetection .
#' @param terminologyMapping .
#' @param exportFormat .
#' @param generateReport .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$overview} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$qualityAssessment} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$consistencyChecks} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$terminologyMapping} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$exportSummary} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$qualityPlot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$overview$asDF}
#'
#' \code{as.data.frame(results$overview)}
#'
#' @export
clinicaldataintegration <- function(
    data,
    dataSource = "csv",
    patientIdVar,
    dateVars,
    clinicalVars,
    qualityCheck = TRUE,
    completenessThreshold = 80,
    consistencyCheck = TRUE,
    outlierDetection = TRUE,
    terminologyMapping = "none",
    exportFormat = "csv",
    generateReport = TRUE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("clinicaldataintegration requires jmvcore to be installed (restart may be required)")

    if ( ! missing(patientIdVar)) patientIdVar <- jmvcore::resolveQuo(jmvcore::enquo(patientIdVar))
    if ( ! missing(dateVars)) dateVars <- jmvcore::resolveQuo(jmvcore::enquo(dateVars))
    if ( ! missing(clinicalVars)) clinicalVars <- jmvcore::resolveQuo(jmvcore::enquo(clinicalVars))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(patientIdVar), patientIdVar, NULL),
            `if`( ! missing(dateVars), dateVars, NULL),
            `if`( ! missing(clinicalVars), clinicalVars, NULL))


    options <- clinicaldataintegrationOptions$new(
        dataSource = dataSource,
        patientIdVar = patientIdVar,
        dateVars = dateVars,
        clinicalVars = clinicalVars,
        qualityCheck = qualityCheck,
        completenessThreshold = completenessThreshold,
        consistencyCheck = consistencyCheck,
        outlierDetection = outlierDetection,
        terminologyMapping = terminologyMapping,
        exportFormat = exportFormat,
        generateReport = generateReport)

    analysis <- clinicaldataintegrationClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

