#' Comparing Survival Outcomes
#'
#' 
#' @param data .
#' @param times .
#' @param status .
#' @param groups .
#' @param ciyn .
#' @param loglogyn .
#' @param timeunits .
#' @param pairwise Perform pairwise log-rank comparisons between all groups
#' @param pairwiseCorrection Method for adjusting p-values for multiple
#'   comparisons
#' @param trendTest Test for trend across ordered groups (assumes groups are
#'   ordered)
#' @param landmarkTime Exclude events before this time (0 = no exclusion)
#' @param landmarkUnit Unit for landmark time
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$compsurvTable1} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$compsurvTable2} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$compsurvTable3} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$pairwiseTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$trendTestTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$landmarkNote} \tab \tab \tab \tab \tab a html \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$compsurvTable1$asDF}
#'
#' \code{as.data.frame(results$compsurvTable1)}
#'
#' @export
comparingSurvival <- function(
    data,
    times = NULL,
    status = NULL,
    groups = NULL,
    ciyn = FALSE,
    loglogyn = FALSE,
    timeunits = "None",
    pairwise = FALSE,
    pairwiseCorrection = "none",
    trendTest = FALSE,
    landmarkTime = 0,
    landmarkUnit = "same") {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("comparingSurvival requires jmvcore to be installed (restart may be required)")

    if ( ! missing(times)) times <- jmvcore::resolveQuo(jmvcore::enquo(times))
    if ( ! missing(status)) status <- jmvcore::resolveQuo(jmvcore::enquo(status))
    if ( ! missing(groups)) groups <- jmvcore::resolveQuo(jmvcore::enquo(groups))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(times), times, NULL),
            `if`( ! missing(status), status, NULL),
            `if`( ! missing(groups), groups, NULL))


    options <- comparingSurvivalOptions$new(
        times = times,
        status = status,
        groups = groups,
        ciyn = ciyn,
        loglogyn = loglogyn,
        timeunits = timeunits,
        pairwise = pairwise,
        pairwiseCorrection = pairwiseCorrection,
        trendTest = trendTest,
        landmarkTime = landmarkTime,
        landmarkUnit = landmarkUnit)

    analysis <- comparingSurvivalClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

