#' Robust Cox Regression
#'
#' Implements robust Cox proportional hazards regression models that are 
#' resistant to outliers and model misspecification. Provides multiple robust 
#' estimation methods including Huber M-estimation, bounded influence 
#' functions, and weighted partial likelihood approaches for reliable survival 
#' analysis in the presence of data anomalies.
#'
#' @examples
#' # Example: Robust Cox regression
#' library(survival)
#' library(coxrobust)
#'
#' coxrobust(
#'     data = lung_data,
#'     elapsedtime = "time",
#'     outcome = "status",
#'     outcomeLevel = "2",
#'     covariates = c("age", "sex", "ph.ecog"),
#'     robust_method = "huber",
#'     tuning_constant = 1.345
#' )
#'
#' @param data the data as a data frame
#' @param elapsedtime Survival time or follow-up duration variable
#' @param outcome Event indicator variable (0/1, FALSE/TRUE, or factor)
#' @param covariates Predictor variables for the Cox model
#' @param outcomeLevel Level of outcome variable indicating event occurrence
#' @param robust_method Robust estimation method to use
#' @param tuning_constant Tuning constant for robust estimation
#'   (method-specific)
#' @param efficiency_target Target efficiency relative to standard Cox model
#' @param max_iterations Maximum number of iterations for robust estimation
#' @param convergence_tolerance Convergence tolerance for parameter estimation
#' @param outlier_detection Perform automatic outlier detection and flagging
#' @param outlier_threshold Threshold for outlier detection (in standard
#'   deviations)
#' @param stratify_variable Variable for stratified analysis
#' @param weights_variable Optional observation weights
#' @param bootstrap_ci Compute bootstrap confidence intervals
#' @param bootstrap_samples Number of bootstrap samples for CI estimation
#' @param confidence_level Confidence level for intervals
#' @param show_model_summary Display comprehensive model summary
#' @param show_coefficients Display coefficient estimates table
#' @param show_outliers Display outlier detection results
#' @param show_influence Display influence diagnostics
#' @param show_comparison Compare robust vs standard Cox models
#' @param show_residual_plots Display residual diagnostic plots
#' @param show_influence_plots Display influence diagnostic plots
#' @param show_weight_plots Display robust weight distributions
#' @param show_survival_plots Display survival curves
#' @param showSummaries Generate natural language summaries
#' @param showExplanations Show detailed methodology explanations
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$modelSummary} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$coefficients} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$outlierAnalysis} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$influenceMeasures} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$modelComparison} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$convergenceInfo} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$residualPlots} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$influencePlots} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$weightPlots} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$survivalPlots} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$outlierPlots} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$comparisonPlots} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$summaryTable} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$methodExplanation} \tab \tab \tab \tab \tab a html \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$modelSummary$asDF}
#'
#' \code{as.data.frame(results$modelSummary)}
#'
#' @export
coxrobust <- function(
    data,
    elapsedtime,
    outcome,
    covariates,
    outcomeLevel = "1",
    robust_method = "huber",
    tuning_constant = 1.345,
    efficiency_target = 0.95,
    max_iterations = 100,
    convergence_tolerance = 0.000001,
    outlier_detection = TRUE,
    outlier_threshold = 3,
    stratify_variable,
    weights_variable,
    bootstrap_ci = FALSE,
    bootstrap_samples = 500,
    confidence_level = 0.95,
    show_model_summary = TRUE,
    show_coefficients = TRUE,
    show_outliers = TRUE,
    show_influence = TRUE,
    show_comparison = TRUE,
    show_residual_plots = TRUE,
    show_influence_plots = TRUE,
    show_weight_plots = TRUE,
    show_survival_plots = TRUE,
    showSummaries = FALSE,
    showExplanations = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("coxrobust requires jmvcore to be installed (restart may be required)")

    if ( ! missing(elapsedtime)) elapsedtime <- jmvcore::resolveQuo(jmvcore::enquo(elapsedtime))
    if ( ! missing(outcome)) outcome <- jmvcore::resolveQuo(jmvcore::enquo(outcome))
    if ( ! missing(covariates)) covariates <- jmvcore::resolveQuo(jmvcore::enquo(covariates))
    if ( ! missing(stratify_variable)) stratify_variable <- jmvcore::resolveQuo(jmvcore::enquo(stratify_variable))
    if ( ! missing(weights_variable)) weights_variable <- jmvcore::resolveQuo(jmvcore::enquo(weights_variable))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(elapsedtime), elapsedtime, NULL),
            `if`( ! missing(outcome), outcome, NULL),
            `if`( ! missing(covariates), covariates, NULL),
            `if`( ! missing(stratify_variable), stratify_variable, NULL),
            `if`( ! missing(weights_variable), weights_variable, NULL))


    options <- coxrobustOptions$new(
        elapsedtime = elapsedtime,
        outcome = outcome,
        covariates = covariates,
        outcomeLevel = outcomeLevel,
        robust_method = robust_method,
        tuning_constant = tuning_constant,
        efficiency_target = efficiency_target,
        max_iterations = max_iterations,
        convergence_tolerance = convergence_tolerance,
        outlier_detection = outlier_detection,
        outlier_threshold = outlier_threshold,
        stratify_variable = stratify_variable,
        weights_variable = weights_variable,
        bootstrap_ci = bootstrap_ci,
        bootstrap_samples = bootstrap_samples,
        confidence_level = confidence_level,
        show_model_summary = show_model_summary,
        show_coefficients = show_coefficients,
        show_outliers = show_outliers,
        show_influence = show_influence,
        show_comparison = show_comparison,
        show_residual_plots = show_residual_plots,
        show_influence_plots = show_influence_plots,
        show_weight_plots = show_weight_plots,
        show_survival_plots = show_survival_plots,
        showSummaries = showSummaries,
        showExplanations = showExplanations)

    analysis <- coxrobustClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

