#' Dendrogram
#'
#' 
#' @param data .
#' @param vars .
#' @param clusterMethod .
#' @param distanceMethod .
#' @param showLabels .
#' @param colorGroups .
#' @param group .
#' @param plotHeight .
#' @param plotWidth .
#' @param plotType .
#' @param edgeType .
#' @param colorScheme .
#' @param highlightClusters .
#' @param nClusters .
#' @param maxLabels .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$clusterInfo} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$summary} \tab \tab \tab \tab \tab a table \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$summary$asDF}
#'
#' \code{as.data.frame(results$summary)}
#'
#' @export
dendrogram <- function(
    data,
    vars,
    clusterMethod = "ward.D2",
    distanceMethod = "euclidean",
    showLabels = TRUE,
    colorGroups = FALSE,
    group,
    plotHeight = 600,
    plotWidth = 800,
    plotType = "linear",
    edgeType = "diagonal",
    colorScheme = "default",
    highlightClusters = FALSE,
    nClusters = 3,
    maxLabels = 50) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("dendrogram requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if ( ! missing(group)) group <- jmvcore::resolveQuo(jmvcore::enquo(group))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL),
            `if`( ! missing(group), group, NULL))

    for (v in group) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- dendrogramOptions$new(
        vars = vars,
        clusterMethod = clusterMethod,
        distanceMethod = distanceMethod,
        showLabels = showLabels,
        colorGroups = colorGroups,
        group = group,
        plotHeight = plotHeight,
        plotWidth = plotWidth,
        plotType = plotType,
        edgeType = edgeType,
        colorScheme = colorScheme,
        highlightClusters = highlightClusters,
        nClusters = nClusters,
        maxLabels = maxLabels)

    analysis <- dendrogramClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

