#' Enhanced Correlation Analysis
#'
#' 
#' @param data .
#' @param vars .
#' @param method .
#' @param ci .
#' @param ciWidth .
#' @param sig .
#' @param sigLevel .
#' @param plotMatrix .
#' @param plotScatter .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$text$asDF}
#'
#' \code{as.data.frame(results$text)}
#'
#' @export
enhancedcorrelation <- function(
    data,
    vars,
    method = "spearman",
    ci = TRUE,
    ciWidth = 95,
    sig = TRUE,
    sigLevel = 0.05,
    plotMatrix = FALSE,
    plotScatter = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("enhancedcorrelation requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL))


    options <- enhancedcorrelationOptions$new(
        vars = vars,
        method = method,
        ci = ci,
        ciWidth = ciWidth,
        sig = sig,
        sigLevel = sigLevel,
        plotMatrix = plotMatrix,
        plotScatter = plotScatter)

    analysis <- enhancedcorrelationClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

