flexiblebaselineBase <- if (requireNamespace("jmvcore", quietly=TRUE)) R6::R6Class(
    "flexiblebaselineBase",
    inherit = jmvcore::Analysis,
    public = list(
        initialize = function(options, data=NULL, datasetId="", analysisId="", revision=0) {
            super$initialize(
                package = "ClinicoPath",
                name = "flexiblebaseline",
                version = c(1,0,0),
                options = options,
                results = flexiblebaselineResults$new(options=options),
                data = data,
                datasetId = datasetId,
                analysisId = analysisId,
                revision = revision,
                pause = NULL,
                completeWhenFilled = FALSE,
                requiresMissings = FALSE,
                weightsSupport = 'auto')
        }))

#' Flexible Baseline Distributions
#'
#' 
#' @param data The data as a data frame.
#' @param elapsedtime Time variable for survival analysis.
#' @param outcome Event indicator variable.
#' @param explanatory Optional grouping variable for stratified analysis.
#' @param covariates Covariates to include in the flexible parametric model.
#' @param outcomeLevel Level of outcome variable indicating event.
#' @param baseline_type Type of flexible baseline distribution modeling
#'   approach.
#' @param spline_knots Number of internal knots for spline-based models.
#' @param knot_placement Method for placing spline knots.
#' @param boundary_knots Include boundary knots at extreme time points.
#' @param spline_degree Degree of B-splines for flexible modeling.
#' @param transformation_family Transformation family for transformation
#'   models.
#' @param confidence_level Confidence level for parameter estimates and
#'   predictions.
#' @param show_model_summary Display comprehensive model summary information.
#' @param show_coefficients Display parameter estimates and standard errors.
#' @param show_spline_basis Display spline basis function details.
#' @param show_survival_curves Display fitted survival curves.
#' @param show_hazard_curves Display estimated hazard function over time.
#' @param show_cumulative_hazard Display cumulative hazard function.
#' @param show_diagnostics Display model diagnostic plots and statistics.
#' @param showSummaries Generate natural language summaries of results.
#' @param showExplanations Show detailed methodology explanations.
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$todo} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$modelSummary} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$coefficientsTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$splineBasisTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$survivalPlot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$hazardPlot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$cumulativeHazardPlot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$diagnosticPlots} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$goodnessOfFitTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$comparisonTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$predictionTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$analysisSummary} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$methodExplanation} \tab \tab \tab \tab \tab a html \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$coefficientsTable$asDF}
#'
#' \code{as.data.frame(results$coefficientsTable)}
#'
#' @export
flexiblebaseline <- function(
    data,
    elapsedtime,
    outcome,
    explanatory,
    covariates,
    outcomeLevel = "1",
    baseline_type = "spline_hazard",
    spline_knots = 2,
    knot_placement = "quantiles",
    boundary_knots = TRUE,
    spline_degree = "cubic",
    transformation_family = "cox",
    confidence_level = 0.95,
    show_model_summary = TRUE,
    show_coefficients = TRUE,
    show_spline_basis = FALSE,
    show_survival_curves = TRUE,
    show_hazard_curves = TRUE,
    show_cumulative_hazard = FALSE,
    show_diagnostics = TRUE,
    showSummaries = FALSE,
    showExplanations = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("flexiblebaseline requires jmvcore to be installed (restart may be required)")

    if ( ! missing(elapsedtime)) elapsedtime <- jmvcore::resolveQuo(jmvcore::enquo(elapsedtime))
    if ( ! missing(outcome)) outcome <- jmvcore::resolveQuo(jmvcore::enquo(outcome))
    if ( ! missing(explanatory)) explanatory <- jmvcore::resolveQuo(jmvcore::enquo(explanatory))
    if ( ! missing(covariates)) covariates <- jmvcore::resolveQuo(jmvcore::enquo(covariates))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(elapsedtime), elapsedtime, NULL),
            `if`( ! missing(outcome), outcome, NULL),
            `if`( ! missing(explanatory), explanatory, NULL),
            `if`( ! missing(covariates), covariates, NULL))


    options <- flexiblebaselineOptions$new(
        elapsedtime = elapsedtime,
        outcome = outcome,
        explanatory = explanatory,
        covariates = covariates,
        outcomeLevel = outcomeLevel,
        baseline_type = baseline_type,
        spline_knots = spline_knots,
        knot_placement = knot_placement,
        boundary_knots = boundary_knots,
        spline_degree = spline_degree,
        transformation_family = transformation_family,
        confidence_level = confidence_level,
        show_model_summary = show_model_summary,
        show_coefficients = show_coefficients,
        show_spline_basis = show_spline_basis,
        show_survival_curves = show_survival_curves,
        show_hazard_curves = show_hazard_curves,
        show_cumulative_hazard = show_cumulative_hazard,
        show_diagnostics = show_diagnostics,
        showSummaries = showSummaries,
        showExplanations = showExplanations)

    analysis <- flexiblebaselineClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

