                        `type`="number", 
                        `format`="zto:4"),
                    list(
                        `name`="bias", 
                        `title`="Bias", 
                        `type`="number", 
                        `format`="zto:4"),
                    list(
                        `name`="percentile_ci", 
                        `title`="Percentile CI", 
                        `type`="text"))))
            self$add(jmvcore::Image$new(
                options=options,
                name="survivalCurves",
                title="Fitted Survival Curves",
                visible="(show_survival_curves)"))
            self$add(jmvcore::Image$new(
