#' IHC Scoring Standardization
#'
#' 
#' @param data the data as a data frame
#' @param intensity_var staining intensity scores (typically 0-3 scale)
#' @param proportion_var percentage of positive cells (0-100\%)
#' @param sample_id_var unique identifier for each sample
#' @param group_var grouping variable for comparative analysis
#' @param scoring_method primary scoring methodology to emphasize
#' @param binary_cutpoint H-score cutpoint for positive/negative
#'   classification
#' @param allred_cutpoint Allred score cutpoint for positive/negative
#'   classification
#' @param intensity_scale intensity scoring scale used
#' @param biomarker_type specific biomarker being analyzed
#' @param show_plots display scoring distribution and correlation plots
#' @param show_agreement_plots display method agreement analysis plots
#' @param include_statistics calculate comprehensive descriptive statistics
#' @param include_digital_validation include digital pathology validation
#'   metrics
#' @param agreement_analysis calculate inter-method agreement statistics
#' @param quality_control perform quality control checks and outlier detection
#' @param clinical_interpretation provide clinical context and interpretation
#' @param export_results format results for external analysis or reporting
#' @param confidence_level confidence level for statistical intervals
#' @param bootstrap_n number of bootstrap replicates for confidence intervals
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$interpretation} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$scorestable} \tab \tab \tab \tab \tab Calculated H-scores, Allred scores, and binary classifications \cr
#'   \code{results$statisticstable} \tab \tab \tab \tab \tab Comprehensive statistical summary for each scoring method \cr
#'   \code{results$agreementtable} \tab \tab \tab \tab \tab Correlation and agreement statistics between scoring methods \cr
#'   \code{results$qualitycontroltable} \tab \tab \tab \tab \tab Outlier detection and data quality assessment \cr
#'   \code{results$distributionplot} \tab \tab \tab \tab \tab Visual distribution of H-scores and Allred scores \cr
#'   \code{results$correlationplot} \tab \tab \tab \tab \tab Correlation between H-score and Allred score methods \cr
#'   \code{results$agreementplot} \tab \tab \tab \tab \tab Bland-Altman style agreement analysis between methods \cr
#'   \code{results$cutpointplot} \tab \tab \tab \tab \tab ROC analysis for optimal cutpoint determination \cr
#'   \code{results$biomarkerspecific$biomarkerresults} \tab \tab \tab \tab \tab Analysis tailored to specific biomarker characteristics \cr
#'   \code{results$biomarkerspecific$clinicalcutpoints} \tab \tab \tab \tab \tab Established clinical thresholds and their performance \cr
#'   \code{results$digitalvalidation$algorithmcomparison} \tab \tab \tab \tab \tab Statistical comparison of digital vs manual scoring \cr
#'   \code{results$digitalvalidation$batcheffects} \tab \tab \tab \tab \tab Analysis of systematic differences across batches \cr
#'   \code{results$digitalvalidation$validationplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$advancedmetrics$reliabilitymetrics} \tab \tab \tab \tab \tab Comprehensive reliability and consistency metrics \cr
#'   \code{results$advancedmetrics$distributionanalysis} \tab \tab \tab \tab \tab Normality testing and distribution characteristics \cr
#'   \code{results$exportdata} \tab \tab \tab \tab \tab Formatted results for external analysis or reporting \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$scorestable$asDF}
#'
#' \code{as.data.frame(results$scorestable)}
#'
#' @export
ihcscoring <- function(
    data,
    intensity_var,
    proportion_var,
    sample_id_var,
    group_var,
    scoring_method = "both",
    binary_cutpoint = 100,
    allred_cutpoint = 3,
    intensity_scale = "standard",
    biomarker_type = "other",
    show_plots = TRUE,
    show_agreement_plots = TRUE,
    include_statistics = TRUE,
    include_digital_validation = FALSE,
    agreement_analysis = TRUE,
    quality_control = TRUE,
    clinical_interpretation = TRUE,
    export_results = FALSE,
    confidence_level = 0.95,
    bootstrap_n = 1000) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("ihcscoring requires jmvcore to be installed (restart may be required)")

    if ( ! missing(intensity_var)) intensity_var <- jmvcore::resolveQuo(jmvcore::enquo(intensity_var))
    if ( ! missing(proportion_var)) proportion_var <- jmvcore::resolveQuo(jmvcore::enquo(proportion_var))
    if ( ! missing(sample_id_var)) sample_id_var <- jmvcore::resolveQuo(jmvcore::enquo(sample_id_var))
    if ( ! missing(group_var)) group_var <- jmvcore::resolveQuo(jmvcore::enquo(group_var))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(intensity_var), intensity_var, NULL),
            `if`( ! missing(proportion_var), proportion_var, NULL),
            `if`( ! missing(sample_id_var), sample_id_var, NULL),
            `if`( ! missing(group_var), group_var, NULL))

    for (v in group_var) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- ihcscoringOptions$new(
        intensity_var = intensity_var,
        proportion_var = proportion_var,
        sample_id_var = sample_id_var,
        group_var = group_var,
        scoring_method = scoring_method,
        binary_cutpoint = binary_cutpoint,
        allred_cutpoint = allred_cutpoint,
        intensity_scale = intensity_scale,
        biomarker_type = biomarker_type,
        show_plots = show_plots,
        show_agreement_plots = show_agreement_plots,
        include_statistics = include_statistics,
        include_digital_validation = include_digital_validation,
        agreement_analysis = agreement_analysis,
        quality_control = quality_control,
        clinical_interpretation = clinical_interpretation,
        export_results = export_results,
        confidence_level = confidence_level,
        bootstrap_n = bootstrap_n)

    analysis <- ihcscoringClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

