#' Arc Diagram
#'
#' 'Creates arc diagrams for network visualization using the arcdiagram 
#' package.'
#' 
#'
#' @examples
#' \donttest{
#' # example will be added
#'}
#' @param data The data as a data frame.
#' @param source .
#' @param target .
#' @param weight .
#' @param group .
#' @param showNodes .
#' @param nodeSize .
#' @param nodeSizeValue .
#' @param sortNodes .
#' @param sortDecreasing .
#' @param horizontal .
#' @param arcWidth .
#' @param arcWidthValue .
#' @param arcTransparency .
#' @param directed .
#' @param colorByGroup .
#' @param showStats .
#' @param showLegend .
#' @param labelSize .
#' @param plotTitle .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$todo} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$networkStats} \tab \tab \tab \tab \tab a html \cr
#' }
#'
#' @export
jjarcdiagram <- function(
    data,
    source,
    target,
    weight = NULL,
    group = NULL,
    showNodes = TRUE,
    nodeSize = "fixed",
    nodeSizeValue = 2,
    sortNodes = "none",
    sortDecreasing = FALSE,
    horizontal = TRUE,
    arcWidth = "fixed",
    arcWidthValue = 1,
    arcTransparency = 0.5,
    directed = TRUE,
    colorByGroup = TRUE,
    showStats = TRUE,
    showLegend = TRUE,
    labelSize = 0.8,
    plotTitle = "") {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("jjarcdiagram requires jmvcore to be installed (restart may be required)")

    if ( ! missing(source)) source <- jmvcore::resolveQuo(jmvcore::enquo(source))
    if ( ! missing(target)) target <- jmvcore::resolveQuo(jmvcore::enquo(target))
    if ( ! missing(weight)) weight <- jmvcore::resolveQuo(jmvcore::enquo(weight))
    if ( ! missing(group)) group <- jmvcore::resolveQuo(jmvcore::enquo(group))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(source), source, NULL),
            `if`( ! missing(target), target, NULL),
            `if`( ! missing(weight), weight, NULL),
            `if`( ! missing(group), group, NULL))

    for (v in source) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])
    for (v in target) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])
    for (v in group) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- jjarcdiagramOptions$new(
        source = source,
        target = target,
        weight = weight,
        group = group,
        showNodes = showNodes,
        nodeSize = nodeSize,
        nodeSizeValue = nodeSizeValue,
        sortNodes = sortNodes,
        sortDecreasing = sortDecreasing,
        horizontal = horizontal,
        arcWidth = arcWidth,
        arcWidthValue = arcWidthValue,
        arcTransparency = arcTransparency,
        directed = directed,
        colorByGroup = colorByGroup,
        showStats = showStats,
        showLegend = showLegend,
        labelSize = labelSize,
        plotTitle = plotTitle)

    analysis <- jjarcdiagramClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

