#' Box-Violin Plots to Compare Between Groups
#'
#' Wrapper Function for ggstatsplot::ggbetweenstats and
#' ggstatsplot::grouped_ggbetweenstats to generate Box-Violin Plots
#' for comparing continuous variables between groups with statistical
#' annotations.
#' 
#'
#' @examples
#' # Basic comparison between groups
#' jjbetweenstats(
#'     data = iris,
#'     dep = "Sepal.Length",
#'     group = "Species",
#'     typestatistics = "parametric",
#'     pairwisecomparisons = TRUE
#' )
#'
#' # Multiple variables comparison
#' jjbetweenstats(
#'     data = mtcars,
#'     dep = c("mpg", "hp", "wt"),
#'     group = "cyl",
#'     typestatistics = "nonparametric",
#'     pairwisecomparisons = TRUE,
#'     pairwisedisplay = "significant",
#'     padjustmethod = "bonferroni"
#' )
#'
#' # Grouped analysis with split variable
#' jjbetweenstats(
#'     data = mtcars,
#'     dep = "mpg",
#'     group = "cyl",
#'     grvar = "am",
#'     typestatistics = "robust",
#'     centralityplotting = TRUE,
#'     centralitytype = "robust",
#' )
#'
#' # Bayesian analysis with custom aesthetics
#' jjbetweenstats(
#'     data = ToothGrowth,
#'     dep = "len",
#'     group = "supp",
#'     grvar = "dose",
#'     typestatistics = "bayes",
#'     bfmessage = TRUE,
#'     mytitle = "Tooth Growth by Supplement Type",
#'     xtitle = "Supplement",
#'     ytitle = "Tooth Length"
#' )
#'
#' @param data The data as a data frame.
#' @param dep .
#' @param group .
#' @param grvar .
#' @param centralityplotting .
#' @param centralitytype .
#' @param typestatistics .
#' @param pairwisecomparisons .
#' @param pairwisedisplay .
#' @param padjustmethod .
#' @param effsizetype .
#' @param mytitle .
#' @param xtitle .
#' @param ytitle .
#' @param originaltheme .
#' @param resultssubtitle .
#' @param bfmessage Whether to display Bayes Factor in the subtitle when using
#'   Bayesian analysis.
#' @param k Number of decimal places for displaying statistics in the
#'   subtitle.
#' @param conflevel Confidence level for confidence intervals.
#' @param varequal Whether to assume equal variances across groups for
#'   parametric tests.
#' @param plotwidth Width of the plot in pixels. Default is 650.
#' @param plotheight Height of the plot in pixels. Default is 450.
#' @param colorblindSafe Whether to use colorblind-safe color palette for plot
#'   elements.
#' @param addGGPubrPlot Add publication-ready plot using ggpubr package. This
#'   provides an alternative visualization with publication-quality aesthetics.
#' @param ggpubrPlotType Type of ggpubr plot to display when addGGPubrPlot is
#'   enabled.
#' @param ggpubrPalette Color palette for ggpubr plot.
#' @param ggpubrAddStats Add statistical comparison p-values to ggpubr plot.
#' @param ggpubrAddPoints Overlay individual data points on ggpubr plot.
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$todo} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$ggpubrPlot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$ggpubrPlot2} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' @export
jjbetweenstats <- function(
    data,
    dep,
    group,
    grvar = NULL,
    centralityplotting = FALSE,
    centralitytype = "parametric",
    typestatistics = "parametric",
    pairwisecomparisons = FALSE,
    pairwisedisplay = "significant",
    padjustmethod = "holm",
    effsizetype = "biased",
    mytitle = "Within Group Comparison",
    xtitle = "",
    ytitle = "",
    originaltheme = FALSE,
    resultssubtitle = FALSE,
    bfmessage = FALSE,
    k = 2,
    conflevel = 0.95,
    varequal = FALSE,
    plotwidth = 650,
    plotheight = 450,
    colorblindSafe = FALSE,
    addGGPubrPlot = FALSE,
    ggpubrPlotType = "boxplot",
    ggpubrPalette = "jco",
    ggpubrAddStats = TRUE,
    ggpubrAddPoints = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("jjbetweenstats requires jmvcore to be installed (restart may be required)")

    if ( ! missing(dep)) dep <- jmvcore::resolveQuo(jmvcore::enquo(dep))
    if ( ! missing(group)) group <- jmvcore::resolveQuo(jmvcore::enquo(group))
    if ( ! missing(grvar)) grvar <- jmvcore::resolveQuo(jmvcore::enquo(grvar))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(dep), dep, NULL),
            `if`( ! missing(group), group, NULL),
            `if`( ! missing(grvar), grvar, NULL))

    for (v in group) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])
    for (v in grvar) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- jjbetweenstatsOptions$new(
        dep = dep,
        group = group,
        grvar = grvar,
        centralityplotting = centralityplotting,
        centralitytype = centralitytype,
        typestatistics = typestatistics,
        pairwisecomparisons = pairwisecomparisons,
        pairwisedisplay = pairwisedisplay,
        padjustmethod = padjustmethod,
        effsizetype = effsizetype,
        mytitle = mytitle,
        xtitle = xtitle,
        ytitle = ytitle,
        originaltheme = originaltheme,
        resultssubtitle = resultssubtitle,
        bfmessage = bfmessage,
        k = k,
        conflevel = conflevel,
        varequal = varequal,
        plotwidth = plotwidth,
        plotheight = plotheight,
        colorblindSafe = colorblindSafe,
        addGGPubrPlot = addGGPubrPlot,
        ggpubrPlotType = ggpubrPlotType,
        ggpubrPalette = ggpubrPalette,
        ggpubrAddStats = ggpubrAddStats,
        ggpubrAddPoints = ggpubrAddPoints)

    analysis <- jjbetweenstatsClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

