#' Dot Chart
#'
#' Wrapper Function for ggstatsplot::ggdotplotstats and
#' ggstatsplot::grouped_ggdotplotstats to generate dot plots
#' comparing continuous variables between groups with statistical
#' annotations and significance testing.
#' 
#'
#' @examples
#' # Basic dot plot with iris dataset
#' jjdotplotstats(
#'     data = iris,
#'     dep = "Sepal.Length",
#'     group = "Species",
#'     typestatistics = "parametric"
#' )
#'
#' # Advanced dot plot with custom settings
#' jjdotplotstats(
#'     data = iris,
#'     dep = "Petal.Width",
#'     group = "Species",
#'     typestatistics = "nonparametric",
#'     centralityplotting = TRUE,
#'     centralitytype = "nonparametric",
#'     testvalueline = TRUE,
#'     testvalue = 1.0,
#'     mytitle = "Petal Width by Species",
#'     xtitle = "Petal Width (cm)",
#'     ytitle = "Species"
#' )
#'
#' # Grouped analysis with mtcars dataset
#' jjdotplotstats(
#'     data = mtcars,
#'     dep = "mpg",
#'     group = "cyl",
#'     grvar = "am",
#'     typestatistics = "robust",
#'     centralityplotting = TRUE,
#'     centralitytype = "robust",
#'     effsizetype = "unbiased",
#'     conflevel = 0.99,
#'     k = 3
#' )
#'
#' # Bayesian analysis with ToothGrowth dataset
#' jjdotplotstats(
#'     data = ToothGrowth,
#'     dep = "len",
#'     group = "supp",
#'     typestatistics = "bayes",
#'     bfmessage = TRUE,
#'     centralityparameter = "mean",
#'     mytitle = "Tooth Growth by Supplement Type"
#' )
#'
#' @param data The data as a data frame.
#' @param dep A continuous numeric variable for which the distribution will be
#'   displayed across different groups using dot plots.
#' @param group A categorical variable that defines the groups for comparison.
#'   Each level will be displayed as a separate group in the dot plot.
#' @param grvar Optional grouping variable to create separate dot plots for
#'   each level of this variable (grouped analysis).
#' @param typestatistics Type of statistical test to perform. 'parametric' for
#'   t-tests, 'nonparametric' for Mann-Whitney U test, 'robust' for robust
#'   tests, 'bayes' for Bayesian analysis.
#' @param effsizetype Type of effect size calculation for parametric tests.
#'   'biased' for Cohen's d, 'unbiased' for Hedge's g, 'eta' for eta-squared,
#'   'omega' for omega-squared.
#' @param centralityplotting .
#' @param centralitytype .
#' @param mytitle .
#' @param xtitle .
#' @param ytitle .
#' @param originaltheme .
#' @param resultssubtitle .
#' @param testvalue A number specifying the value of the null hypothesis for
#'   one-sample tests.
#' @param bfmessage Whether to display Bayes Factor in the subtitle when using
#'   Bayesian analysis.
#' @param conflevel Confidence level for confidence intervals.
#' @param k Number of decimal places for displaying statistics in the
#'   subtitle.
#' @param testvalueline Whether to display a vertical line at the test value.
#' @param centralityparameter Which measure of central tendency to display as
#'   a vertical line.
#' @param centralityk Number of decimal places for centrality parameter label.
#' @param plotwidth Width of the plot in pixels. Default is 650.
#' @param plotheight Height of the plot in pixels. Default is 450.
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$todo} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' @export
jjdotplotstats <- function(
    data,
    dep,
    group,
    grvar,
    typestatistics = "parametric",
    effsizetype = "biased",
    centralityplotting = FALSE,
    centralitytype = "parametric",
    mytitle = "",
    xtitle = "",
    ytitle = "",
    originaltheme = FALSE,
    resultssubtitle = TRUE,
    testvalue = 0,
    bfmessage = TRUE,
    conflevel = 0.95,
    k = 2,
    testvalueline = FALSE,
    centralityparameter = "mean",
    centralityk = 2,
    plotwidth = 650,
    plotheight = 450) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("jjdotplotstats requires jmvcore to be installed (restart may be required)")

    if ( ! missing(dep)) dep <- jmvcore::resolveQuo(jmvcore::enquo(dep))
    if ( ! missing(group)) group <- jmvcore::resolveQuo(jmvcore::enquo(group))
    if ( ! missing(grvar)) grvar <- jmvcore::resolveQuo(jmvcore::enquo(grvar))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(dep), dep, NULL),
            `if`( ! missing(group), group, NULL),
            `if`( ! missing(grvar), grvar, NULL))

    for (v in group) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])
    for (v in grvar) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- jjdotplotstatsOptions$new(
        dep = dep,
        group = group,
        grvar = grvar,
        typestatistics = typestatistics,
        effsizetype = effsizetype,
        centralityplotting = centralityplotting,
        centralitytype = centralitytype,
        mytitle = mytitle,
        xtitle = xtitle,
        ytitle = ytitle,
        originaltheme = originaltheme,
        resultssubtitle = resultssubtitle,
        testvalue = testvalue,
        bfmessage = bfmessage,
        conflevel = conflevel,
        k = k,
        testvalueline = testvalueline,
        centralityparameter = centralityparameter,
        centralityk = centralityk,
        plotwidth = plotwidth,
        plotheight = plotheight)

    analysis <- jjdotplotstatsClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

