#' Scatter Plot
#'
#' Wrapper Function for ggstatsplot::ggscatterstats and
#' ggstatsplot::grouped_ggscatterstats to generate scatter plots
#' with correlation analysis and optional marginal distributions.
#' 
#'
#' @examples
#' \donttest{
#' # Load test data
#' data("mtcars")
#'
#' # Basic scatter plot with correlation
#' jjscatterstats(
#'   data = mtcars,
#'   dep = "mpg",       # x-axis
#'   group = "hp",      # y-axis
#'   typestatistics = "parametric",
#'   conflevel = 0.95,
#'   k = 2
#' )
#'
#' # Scatter plot with marginal histograms
#' jjscatterstats(
#'   data = mtcars,
#'   dep = "mpg",
#'   group = "hp",
#'   marginal = TRUE,
#'   xsidefill = "#009E73",
#'   ysidefill = "#D55E00",
#'   pointsize = 4,
#'   pointalpha = 0.6,
#'   smoothlinesize = 2,
#'   smoothlinecolor = "red"
#' )
#'
#' # Grouped scatter plot by number of cylinders
#' jjscatterstats(
#'   data = mtcars,
#'   dep = "mpg",
#'   group = "hp",
#'   grvar = "cyl",
#'   typestatistics = "nonparametric",
#'   bfmessage = FALSE,
#'   resultssubtitle = TRUE
#' )
#'}
#' @param data The data as a data frame.
#' @param dep .
#' @param group .
#' @param grvar .
#' @param typestatistics .
#' @param mytitle .
#' @param xtitle .
#' @param ytitle .
#' @param originaltheme .
#' @param resultssubtitle .
#' @param conflevel Confidence level for confidence intervals (between 0 and
#'   1).
#' @param bfmessage Whether to display Bayes Factor in the subtitle when using
#'   Bayesian analysis.
#' @param k Number of decimal places for displaying statistics in the
#'   subtitle.
#' @param marginal Whether to display marginal histogram plots on the axes
#'   using ggside.
#' @param xsidefill Fill color for x-axis marginal histogram.
#' @param ysidefill Fill color for y-axis marginal histogram.
#' @param pointsize Size of the scatter plot points.
#' @param pointalpha Transparency level for scatter plot points.
#' @param smoothlinesize Width of the regression/smooth line.
#' @param smoothlinecolor Color of the regression/smooth line.
#' @param plotwidth Width of the plot in pixels. Default is 600.
#' @param plotheight Height of the plot in pixels. Default is 450.
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$todo} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' @export
jjscatterstats <- function(
    data,
    dep,
    group,
    grvar,
    typestatistics = "parametric",
    mytitle = "",
    xtitle = "",
    ytitle = "",
    originaltheme = FALSE,
    resultssubtitle = TRUE,
    conflevel = 0.95,
    bfmessage = TRUE,
    k = 2,
    marginal = TRUE,
    xsidefill = "#009E73",
    ysidefill = "#D55E00",
    pointsize = 3,
    pointalpha = 0.4,
    smoothlinesize = 1.5,
    smoothlinecolor = "blue",
    plotwidth = 600,
    plotheight = 450) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("jjscatterstats requires jmvcore to be installed (restart may be required)")

    if ( ! missing(dep)) dep <- jmvcore::resolveQuo(jmvcore::enquo(dep))
    if ( ! missing(group)) group <- jmvcore::resolveQuo(jmvcore::enquo(group))
    if ( ! missing(grvar)) grvar <- jmvcore::resolveQuo(jmvcore::enquo(grvar))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(dep), dep, NULL),
            `if`( ! missing(group), group, NULL),
            `if`( ! missing(grvar), grvar, NULL))

    for (v in grvar) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- jjscatterstatsOptions$new(
        dep = dep,
        group = group,
        grvar = grvar,
        typestatistics = typestatistics,
        mytitle = mytitle,
        xtitle = xtitle,
        ytitle = ytitle,
        originaltheme = originaltheme,
        resultssubtitle = resultssubtitle,
        conflevel = conflevel,
        bfmessage = bfmessage,
        k = k,
        marginal = marginal,
        xsidefill = xsidefill,
        ysidefill = ysidefill,
        pointsize = pointsize,
        pointalpha = pointalpha,
        smoothlinesize = smoothlinesize,
        smoothlinecolor = smoothlinecolor,
        plotwidth = plotwidth,
        plotheight = plotheight)

    analysis <- jjscatterstatsClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

