#' Recoding
#'
#' 
#' @param dep .
#' @param recode_rules One rule per line. Format: oldvalue -> newvalue
#' @param else_level .
#' @param new_var_name Name for the recoded variable
#' @param show_code .
#' @param show_table .
#' @param show_levels .
#' @param recoded_output .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$levels_table} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$notices} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$code_output} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$comparison} \tab \tab \tab \tab \tab a table \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$levels_table$asDF}
#'
#' \code{as.data.frame(results$levels_table)}
#'
#' @export
jrecode <- function(
    dep,
    recode_rules = "",
    else_level = "copy",
    new_var_name = "",
    show_code = TRUE,
    show_table = TRUE,
    show_levels = TRUE,
    recoded_output = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("jrecode requires jmvcore to be installed (restart may be required)")

    if ( ! missing(dep)) dep <- jmvcore::resolveQuo(jmvcore::enquo(dep))

    options <- jrecodeOptions$new(
        dep = dep,
        recode_rules = recode_rules,
        else_level = else_level,
        new_var_name = new_var_name,
        show_code = show_code,
        show_table = show_table,
        show_levels = show_levels,
        recoded_output = recoded_output)

    analysis <- jrecodeClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

