                        `name`="case_id", 
                        `title`="Case ID", 
                        `type`="text"),
                    list(
                        `name`="disagreement_score", 
                        `title`="Disagreement Score", 
                        `type`="number", 
                        `format`="zto"),
                    list(
                        `name`="entropy", 
                        `title`="Entropy", 
                        `type`="number", 
                        `format`="zto"),
                    list(
                        `name`="style_group_patterns", 
                        `title`="Style Group Diagnostic Patterns", 
                        `type`="text"),
                    list(
                        `name`="difficulty_level", 
                        `title`="Case Difficulty", 
                        `type`="text"))))
            self$add(jmvcore::Table$new(
                options=options,
                name="characteristicAssociations",
                title="Style Group Associations with Rater Characteristics",
                visible="(performClustering)",
                columns=list(
                    list(
                        `name`="characteristic", 
                        `title`="Rater Characteristic", 
                        `type`="text"),
                    list(
                        `name`="test_statistic", 
                        `title`="Test Statistic", 
                        `type`="number", 
                        `format`="zto"),
                    list(
                        `name`="df", 
                        `title`="df", 
                        `type`="integer"),
                    list(
                        `name`="p_value", 
                        `title`="p-value", 
                        `type`="number", 
                        `format`="zto,pvalue"),
                    list(
                        `name`="effect_size", 
                        `title`="Effect Size", 
                        `type`="number", 
                        `format`="zto"),
                    list(
                        `name`="interpretation", 
