#' Polychoric Correlation Analysis
#'
#' 
#' @param data .
#' @param vars .
#' @param corrType .
#' @param method .
#' @param ci .
#' @param ciWidth .
#' @param sig .
#' @param sigLevel .
#' @param matrixPlot .
#' @param showFreq .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$correlations} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$frequencies} \tab \tab \tab \tab \tab an array of tables \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$correlations$asDF}
#'
#' \code{as.data.frame(results$correlations)}
#'
#' @export
polychoriccorr <- function(
    data,
    vars,
    corrType = "polychoric",
    method = "ml",
    ci = TRUE,
    ciWidth = 95,
    sig = TRUE,
    sigLevel = 0.05,
    matrixPlot = FALSE,
    showFreq = TRUE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("polychoriccorr requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL))

    for (v in vars) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- polychoriccorrOptions$new(
        vars = vars,
        corrType = corrType,
        method = method,
        ci = ci,
        ciWidth = ciWidth,
        sig = sig,
        sigLevel = sigLevel,
        matrixPlot = matrixPlot,
        showFreq = showFreq)

    analysis <- polychoriccorrClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

