survivalcontBase <- if (requireNamespace("jmvcore", quietly=TRUE)) R6::R6Class(
    "survivalcontBase",
    inherit = jmvcore::Analysis,
    public = list(
        initialize = function(options, data=NULL, datasetId="", analysisId="", revision=0) {
            super$initialize(
                package = "ClinicoPath",
                name = "survivalcont",
                version = c(0,0,31),
                options = options,
                results = survivalcontResults$new(options=options),
                data = data,
                datasetId = datasetId,
                analysisId = analysisId,
                revision = revision,
                pause = NULL,
                completeWhenFilled = FALSE,
                requiresMissings = FALSE,
                weightsSupport = 'none')
        }))

#' Survival Analysis for Continuous Variable
#'
#' 
#' @param data The data as a data frame.
#' @param elapsedtime The time-to-event or follow-up duration for each
#'   patient. Should be numeric and continuous, measured in consistent units
#'   (e.g., months or years). Can be calculated automatically from dates if
#'   using the date options below.
#' @param tint Enable this option if you want to calculate survival time from
#'   dates in your data. This is useful when you have separate columns for
#'   diagnosis date and follow-up date and want to calculate the time elapsed
#'   between them.
#' @param dxdate The date of diagnosis or study entry. Accepts: (1)
#'   Date/datetime text (e.g., "2024-01-15"), (2) Numeric Unix epoch seconds
#'   (from DateTime Converter's corrected_datetime_numeric output), (3) Numeric
#'   datetime values from R. Time intervals calculated as difference from
#'   follow-up date.
#' @param fudate The date of last follow-up or event. Accepts: (1)
#'   Date/datetime text (e.g., "2024-01-15"), (2) Numeric Unix epoch seconds
#'   (from DateTime Converter's corrected_datetime_numeric output), (3) Numeric
#'   datetime values from R. Must be in same format as diagnosis date.
#' @param contexpl The continuous explanatory variable to be used in the
#'   analysis.
#' @param outcome The outcome variable to be used in the analysis.
#' @param outcomeLevel The level of the outcome variable that represents the
#'   event of interest.
#' @param dod .
#' @param dooc .
#' @param awd .
#' @param awod Select the levels of the outcome variable that correspond to
#'   different event types in your data. For example, you might have separate
#'   levels for "Dead of Disease" and "Alive w Disease" in a survival analysis
#'   of cancer patients.
#' @param analysistype Select the type of survival analysis to perform.
#'   "Overall" survival analysis considers all events as equivalent, while
#'   "Cause Specific" analysis distinguishes between different event types.
#'   "Competing Risk" analysis accounts for competing risks that may prevent the
#'   event of interest from occurring.
#' @param cutp Specify the time points for survival probability calculations.
#'   The default "12, 36, 60"  represents 1, 3, and 5 years (compatible with
#'   literature standards). You can customize  these values by entering your own
#'   comma-separated time points (e.g., "6, 18, 30" for  6 months, 18 months, 30
#'   months). Use "default" to restore standard 1,3,5-year analysis.
#' @param timetypedata select the time type in data
#' @param timetypeoutput select the time type in output
#' @param uselandmark Enable this option to perform landmark survival analysis
#'   at a specified time point.
#' @param landmark Specify the landmark time at which to evaluate survival
#'   probabilities in landmark analysis. This option is only available if you
#'   enable the "Use Landmark Time" option.
#' @param sc Enable this option to create a Kaplan-Meier survival plot for the
#'   continuous explanatory variable.
#' @param kmunicate Enable this option to create a KMunicate-style survival
#'   plot for the continuous explanatory variable.
#' @param ce Enable this option to create a plot of cumulative events over
#'   time for the continuous explanatory variable.
#' @param ch Enable this option to create a plot of cumulative hazard over
#'   time for the continuous explanatory variable.
#' @param endplot Specify the end time for the survival plots. This option
#'   determines the maximum time point to include in the plots.
#' @param ybegin_plot Specify the starting value for the y-axis in the
#'   survival plots. This option allows you to customize the range of the
#'   y-axis.
#' @param yend_plot Specify the ending value for the y-axis in the survival
#'   plots. This option allows you to customize the range of the y-axis.
#' @param byplot Specify the time interval for the survival plots. This option
#'   determines the spacing of tick marks on the x-axis.
#' @param findcut Enable this option to automatically find the optimal cut-off
#'   point for the continuous explanatory variable using the maximally selected
#'   rank statistic. This option is only available if you enable the "Survival
#'   Plot" option. The optimal cut-off point will be displayed on the survival
#'   plot as a vertical dashed line.
#' @param multiple_cutoffs Enable this option to find multiple optimal cut-off
#'   points for the continuous explanatory variable. This extends the single
#'   cutoff analysis to identify 2-4 optimal cut-off points that maximize
#'   survival group separation. Creates stratified groups for enhanced survival
#'   analysis.
#' @param num_cutoffs Select the number of cut-off points to identify. This
#'   will create multiple risk groups for stratified survival analysis (e.g., 2
#'   cut-offs create Low, Medium, High risk groups).
#' @param cutoff_method Method for finding multiple cut-offs. Quantile-based
#'   uses tertiles/quartiles, Recursive finds sequential optimal points,
#'   Tree-based uses survival trees, Minimum P-value finds points that minimize
#'   log-rank p-values.
#' @param min_group_size Minimum percentage of patients required in each group
#'   created by cut-offs. Prevents creating groups with insufficient sample
#'   sizes for reliable analysis.
#' @param multievent Enable this option if your data includes multiple event
#'   levels (e.g., different types of events or outcomes). This option is
#'   required for cause-specific and competing risk survival analyses.
#' @param ci95 Enable this option to display 95\% confidence intervals around
#'   the survival estimates in the plots.
#' @param risktable Enable this option to display a table of risk estimates
#'   for each group in the survival analysis.
#' @param censored Enable this option to display censored observations in the
#'   survival plots.
#' @param medianline If true, displays a line indicating the median survival
#'   time on the survival plot.
#' @param person_time Enable this option to calculate and display person-time
#'   metrics, including total follow-up time and incidence rates. These metrics
#'   help quantify the rate of events per unit of time in your study population.
#' @param time_intervals Specify time intervals for stratified person-time
#'   analysis. Enter a  comma-separated list of time points to create intervals.
#'   For example,  "12, 36, 60" will create intervals 0-12, 12-36, 36-60, and
#'   60+.
#' @param rate_multiplier Specify the multiplier for incidence rates (e.g.,
#'   100 for rates per 100 person-years, 1000 for rates per 1000 person-years).
#' @param rmst_analysis Enable Restricted Mean Survival Time (RMST) analysis.
#'   RMST provides the average  survival time up to a specified time horizon,
#'   useful when median survival is undefined.
#' @param rmst_tau Specify the time horizon for RMST calculation. If left as
#'   0, will use 75th percentile  of observed survival times. This represents
#'   the maximum follow-up time for RMST calculation.
#' @param residual_diagnostics Enable Cox model residual diagnostics including
#'   Martingale, Deviance, Score, and  Schoenfeld residuals. Useful for checking
#'   model assumptions and identifying outliers.
#' @param stratified_cox Enable stratified Cox regression analysis. This
#'   allows for different baseline hazards  across strata while maintaining
#'   proportional hazards within strata.
#' @param strata_variable Variable to use for stratification in Cox
#'   regression. Should be a categorical variable  that defines different risk
#'   groups or populations.
#' @param loglog Enable log-log plot for assessing proportional hazards
#'   assumption. Parallel lines  in the log-log plot suggest that proportional
#'   hazards assumption holds.
#' @param showExplanations Display detailed explanations for each analysis
#'   component to help interpret the statistical methods and results.
#' @param showSummaries Display natural language summaries alongside tables
#'   and plots. These summaries provide plain-language interpretations of the
#'   statistical results. Turn off to reduce visual clutter when summaries are
#'   not needed.
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$todo} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$clinicalWarnings} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$coxRegressionHeading} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$coxSummary} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$coxTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$tCoxtext2} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$coxRegressionHeading3} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$coxRegressionExplanation} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$personTimeHeading} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$personTimeTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$personTimeSummary} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$personTimeExplanation} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$rmstHeading} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$rmstTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$rmstSummary} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$rmstExplanation} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$residualsTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$residualDiagnosticsExplanation} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$cutoffAnalysisHeading} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$rescutTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$cutoffAnalysisHeading3} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$cutoffAnalysisExplanation} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$plot4} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot5} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$medianSummary} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$medianTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$survTableSummary} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$survTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot3} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot6} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$survivalPlotsHeading3} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$survivalPlotsExplanation} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$plot7} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$loglogPlotExplanation} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$residualsPlot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$calculatedtime} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$outcomeredefined} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$calculatedcutoff} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$multipleCutTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$multipleMedianTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$multipleCutoffsExplanation} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$multipleSurvTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plotMultipleCutoffs} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plotMultipleSurvival} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$calculatedmulticut} \tab \tab \tab \tab \tab an output \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$coxTable$asDF}
#'
#' \code{as.data.frame(results$coxTable)}
#'
#' @export
survivalcont <- function(
    data,
    elapsedtime,
    tint = FALSE,
    dxdate,
    fudate,
    contexpl,
    outcome,
    outcomeLevel,
    dod,
    dooc,
    awd,
    awod,
    analysistype = "overall",
    cutp = "12, 36, 60",
    timetypedata = "ymd",
    timetypeoutput = "months",
    uselandmark = FALSE,
    landmark = 3,
    sc = FALSE,
    kmunicate = FALSE,
    ce = FALSE,
    ch = FALSE,
    endplot = 60,
    ybegin_plot = 0,
    yend_plot = 1,
    byplot = 12,
    findcut = FALSE,
    multiple_cutoffs = FALSE,
    num_cutoffs = "two",
    cutoff_method = "quantile",
    min_group_size = 10,
    multievent = FALSE,
    ci95 = FALSE,
    risktable = FALSE,
    censored = FALSE,
    medianline = "none",
    person_time = FALSE,
    time_intervals = "12, 36, 60",
    rate_multiplier = 100,
    rmst_analysis = FALSE,
    rmst_tau = 0,
    residual_diagnostics = FALSE,
    stratified_cox = FALSE,
    strata_variable,
    loglog = FALSE,
    showExplanations = FALSE,
    showSummaries = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("survivalcont requires jmvcore to be installed (restart may be required)")

    if ( ! missing(elapsedtime)) elapsedtime <- jmvcore::resolveQuo(jmvcore::enquo(elapsedtime))
    if ( ! missing(dxdate)) dxdate <- jmvcore::resolveQuo(jmvcore::enquo(dxdate))
    if ( ! missing(fudate)) fudate <- jmvcore::resolveQuo(jmvcore::enquo(fudate))
    if ( ! missing(contexpl)) contexpl <- jmvcore::resolveQuo(jmvcore::enquo(contexpl))
    if ( ! missing(outcome)) outcome <- jmvcore::resolveQuo(jmvcore::enquo(outcome))
    if ( ! missing(strata_variable)) strata_variable <- jmvcore::resolveQuo(jmvcore::enquo(strata_variable))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(elapsedtime), elapsedtime, NULL),
            `if`( ! missing(dxdate), dxdate, NULL),
            `if`( ! missing(fudate), fudate, NULL),
            `if`( ! missing(contexpl), contexpl, NULL),
            `if`( ! missing(outcome), outcome, NULL),
            `if`( ! missing(strata_variable), strata_variable, NULL))


    options <- survivalcontOptions$new(
        elapsedtime = elapsedtime,
        tint = tint,
        dxdate = dxdate,
        fudate = fudate,
        contexpl = contexpl,
        outcome = outcome,
        outcomeLevel = outcomeLevel,
        dod = dod,
        dooc = dooc,
        awd = awd,
        awod = awod,
        analysistype = analysistype,
        cutp = cutp,
        timetypedata = timetypedata,
        timetypeoutput = timetypeoutput,
        uselandmark = uselandmark,
        landmark = landmark,
        sc = sc,
        kmunicate = kmunicate,
        ce = ce,
        ch = ch,
        endplot = endplot,
        ybegin_plot = ybegin_plot,
        yend_plot = yend_plot,
        byplot = byplot,
        findcut = findcut,
        multiple_cutoffs = multiple_cutoffs,
        num_cutoffs = num_cutoffs,
        cutoff_method = cutoff_method,
        min_group_size = min_group_size,
        multievent = multievent,
        ci95 = ci95,
        risktable = risktable,
        censored = censored,
        medianline = medianline,
        person_time = person_time,
        time_intervals = time_intervals,
        rate_multiplier = rate_multiplier,
        rmst_analysis = rmst_analysis,
        rmst_tau = rmst_tau,
        residual_diagnostics = residual_diagnostics,
        stratified_cox = stratified_cox,
        strata_variable = strata_variable,
        loglog = loglog,
        showExplanations = showExplanations,
        showSummaries = showSummaries)

    analysis <- survivalcontClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

