#' Tidy Plots
#'
#' 
#' @param data The data as a data frame.
#' @param xvar .
#' @param yvar .
#' @param color .
#' @param plotType .
#' @param pointType .
#' @param showMean .
#' @param showSEM .
#' @param showCI .
#' @param colorScheme .
#' @param plotTitle .
#' @param xLabel .
#' @param yLabel .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' @export
tidyplots <- function(
    data,
    xvar,
    yvar,
    color,
    plotType = "points",
    pointType = "basic",
    showMean = FALSE,
    showSEM = FALSE,
    showCI = FALSE,
    colorScheme = "friendly",
    plotTitle = "",
    xLabel = "",
    yLabel = "") {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("tidyplots requires jmvcore to be installed (restart may be required)")

    if ( ! missing(xvar)) xvar <- jmvcore::resolveQuo(jmvcore::enquo(xvar))
    if ( ! missing(yvar)) yvar <- jmvcore::resolveQuo(jmvcore::enquo(yvar))
    if ( ! missing(color)) color <- jmvcore::resolveQuo(jmvcore::enquo(color))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(xvar), xvar, NULL),
            `if`( ! missing(yvar), yvar, NULL),
            `if`( ! missing(color), color, NULL))


    options <- tidyplotsOptions$new(
        xvar = xvar,
        yvar = yvar,
        color = color,
        plotType = plotType,
        pointType = pointType,
        showMean = showMean,
        showSEM = showSEM,
        showCI = showCI,
        colorScheme = colorScheme,
        plotTitle = plotTitle,
        xLabel = xLabel,
        yLabel = yLabel)

    analysis <- tidyplotsClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

