tidyplotsBase <- if (requireNamespace("jmvcore", quietly=TRUE)) R6::R6Class(
    "tidyplotsBase",
    inherit = jmvcore::Analysis,
    public = list(
        initialize = function(options, data=NULL, datasetId="", analysisId="", revision=0) {
            super$initialize(
                package = "ClinicoPath",
                name = "tidyplots",
                version = c(0,0,31),
                options = options,
                results = tidyplotsResults$new(options=options),
                data = data,
                datasetId = datasetId,
                analysisId = analysisId,
                revision = revision,
                pause = NULL,
                completeWhenFilled = FALSE,
                requiresMissings = FALSE,
                weightsSupport = 'auto')
        }))

#' Comprehensive Tidy Plots
#'
#' Create publication-ready plots using the tidyplots framework with extensive 
#' customization options, statistical features, and advanced visualization 
#' capabilities. Comprehensive implementation of tidyplots for jamovi with 
#' advanced statistical visualization features including multiple plot types, 
#' statistical testing, and extensive customization options.
#' 
#' @param data The data as a data frame.
#' @param xvar .
#' @param yvar .
#' @param color .
#' @param group .
#' @param facet .
#' @param plotType .
#' @param pointType .
#' @param lineType .
#' @param barType .
#' @param stackType .
#' @param showMean .
#' @param meanType .
#' @param showMedian .
#' @param medianType .
#' @param showSum .
#' @param sumType .
#' @param showCount .
#' @param countType .
#' @param showSEM .
#' @param semType .
#' @param showSD .
#' @param sdType .
#' @param showCI .
#' @param ciType .
#' @param showRange .
#' @param rangeType .
#' @param showDistribution .
#' @param distributionType .
#' @param violinPoints .
#' @param histogramBins .
#' @param showPValue .
#' @param showSignificance .
#' @param showReferenceLines .
#' @param referenceX .
#' @param referenceY .
#' @param colorScheme .
#' @param alpha .
#' @param fontSize .
#' @param plotTitle .
#' @param xLabel .
#' @param yLabel .
#' @param legendTitle .
#' @param plotCaption .
#' @param showHowTo .
#' @param showGlossary .
#' @param showSummary .
#' @param pointShape .
#' @param removeLegend .
#' @param removeLegendTitle .
#' @param removePadding .
#' @param removeXAxis .
#' @param removeXAxisLabels .
#' @param removeXAxisTitle .
#' @param removeYAxis .
#' @param removeYAxisLabels .
#' @param removeYAxisTitle .
#' @param sortXAxisLabels .
#' @param reverseXAxisLabels .
#' @param plotTheme .
#' @param removeXAxisLine .
#' @param removeYAxisLine .
#' @param showDataLabels .
#' @param dataLabelType .
#' @param legendPosition .
#' @param flipOrientation .
#' @param paddingTop .
#' @param paddingBottom .
#' @param paddingLeft .
#' @param paddingRight .
#' @param colorSaturation .
#' @param dodgeWidth .
#' @param pointSize .
#' @param pointWhiteBorder .
#' @param lineWidth .
#' @param useRasterization .
#' @param rasterDPI .
#' @param setXLimits .
#' @param xMin .
#' @param xMax .
#' @param setYLimits .
#' @param yMin .
#' @param yMax .
#' @param xAxisTickCount .
#' @param yAxisTickCount .
#' @param fontFamily .
#' @param titleAlignment .
#' @param captionAlignment .
#' @param showMedianLine .
#' @param connectPairedData .
#' @param highlightSubset .
#' @param maxDataPoints .
#' @param errorBarWidth .
#' @param violinScale .
#' @param boxplotOutliers .
#' @param boxplotNotch .
#' @param addSmoother .
#' @param smootherMethod .
#' @param smootherSE .
#' @param plotWidth .
#' @param plotHeight .
#' @param useAutoSize .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$howto} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$glossary} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$summary} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' @export
tidyplots <- function(
    data,
    xvar,
    yvar,
    color,
    group,
    facet,
    plotType = "points",
    pointType = "basic",
    lineType = "direct",
    barType = "mean",
    stackType = "absolute",
    showMean = FALSE,
    meanType = "dash",
    showMedian = FALSE,
    medianType = "dash",
    showSum = FALSE,
    sumType = "dash",
    showCount = FALSE,
    countType = "dash",
    showSEM = FALSE,
    semType = "errorbar",
    showSD = FALSE,
    sdType = "errorbar",
    showCI = FALSE,
    ciType = "errorbar",
    showRange = FALSE,
    rangeType = "errorbar",
    showDistribution = FALSE,
    distributionType = "density",
    violinPoints = FALSE,
    histogramBins = 30,
    showPValue = FALSE,
    showSignificance = FALSE,
    showReferenceLines = FALSE,
    referenceX = "",
    referenceY = "",
    colorScheme = "friendly",
    alpha = 1,
    fontSize = 12,
    plotTitle = "",
    xLabel = "",
    yLabel = "",
    legendTitle = "",
    plotCaption = "",
    showHowTo = FALSE,
    showGlossary = FALSE,
    showSummary = FALSE,
    pointShape = 16,
    removeLegend = FALSE,
    removeLegendTitle = FALSE,
    removePadding = FALSE,
    removeXAxis = FALSE,
    removeXAxisLabels = FALSE,
    removeXAxisTitle = FALSE,
    removeYAxis = FALSE,
    removeYAxisLabels = FALSE,
    removeYAxisTitle = FALSE,
    sortXAxisLabels = FALSE,
    reverseXAxisLabels = FALSE,
    plotTheme = "default",
    removeXAxisLine = FALSE,
    removeYAxisLine = FALSE,
    showDataLabels = FALSE,
    dataLabelType = "direct",
    legendPosition = "default",
    flipOrientation = FALSE,
    paddingTop = 0.1,
    paddingBottom = 0.1,
    paddingLeft = 0.1,
    paddingRight = 0.1,
    colorSaturation = 1,
    dodgeWidth = 0.8,
    pointSize = 2.5,
    pointWhiteBorder = FALSE,
    lineWidth = 1,
    useRasterization = FALSE,
    rasterDPI = 300,
    setXLimits = FALSE,
    xMin = 0,
    xMax = 100,
    setYLimits = FALSE,
    yMin = 0,
    yMax = 100,
    xAxisTickCount = 5,
    yAxisTickCount = 5,
    fontFamily = "default",
    titleAlignment = "left",
    captionAlignment = "right",
    showMedianLine = FALSE,
    connectPairedData = FALSE,
    highlightSubset = FALSE,
    maxDataPoints = 10000,
    errorBarWidth = 0.1,
    violinScale = "area",
    boxplotOutliers = TRUE,
    boxplotNotch = FALSE,
    addSmoother = FALSE,
    smootherMethod = "loess",
    smootherSE = TRUE,
    plotWidth = 150,
    plotHeight = 100,
    useAutoSize = TRUE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("tidyplots requires jmvcore to be installed (restart may be required)")

    if ( ! missing(xvar)) xvar <- jmvcore::resolveQuo(jmvcore::enquo(xvar))
    if ( ! missing(yvar)) yvar <- jmvcore::resolveQuo(jmvcore::enquo(yvar))
    if ( ! missing(color)) color <- jmvcore::resolveQuo(jmvcore::enquo(color))
    if ( ! missing(group)) group <- jmvcore::resolveQuo(jmvcore::enquo(group))
    if ( ! missing(facet)) facet <- jmvcore::resolveQuo(jmvcore::enquo(facet))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(xvar), xvar, NULL),
            `if`( ! missing(yvar), yvar, NULL),
            `if`( ! missing(color), color, NULL),
            `if`( ! missing(group), group, NULL),
            `if`( ! missing(facet), facet, NULL))


    options <- tidyplotsOptions$new(
        xvar = xvar,
        yvar = yvar,
        color = color,
        group = group,
        facet = facet,
        plotType = plotType,
        pointType = pointType,
        lineType = lineType,
        barType = barType,
        stackType = stackType,
        showMean = showMean,
        meanType = meanType,
        showMedian = showMedian,
        medianType = medianType,
        showSum = showSum,
        sumType = sumType,
        showCount = showCount,
        countType = countType,
        showSEM = showSEM,
        semType = semType,
        showSD = showSD,
        sdType = sdType,
        showCI = showCI,
        ciType = ciType,
        showRange = showRange,
        rangeType = rangeType,
        showDistribution = showDistribution,
        distributionType = distributionType,
        violinPoints = violinPoints,
        histogramBins = histogramBins,
        showPValue = showPValue,
        showSignificance = showSignificance,
        showReferenceLines = showReferenceLines,
        referenceX = referenceX,
        referenceY = referenceY,
        colorScheme = colorScheme,
        alpha = alpha,
        fontSize = fontSize,
        plotTitle = plotTitle,
        xLabel = xLabel,
        yLabel = yLabel,
        legendTitle = legendTitle,
        plotCaption = plotCaption,
        showHowTo = showHowTo,
        showGlossary = showGlossary,
        showSummary = showSummary,
        pointShape = pointShape,
        removeLegend = removeLegend,
        removeLegendTitle = removeLegendTitle,
        removePadding = removePadding,
        removeXAxis = removeXAxis,
        removeXAxisLabels = removeXAxisLabels,
        removeXAxisTitle = removeXAxisTitle,
        removeYAxis = removeYAxis,
        removeYAxisLabels = removeYAxisLabels,
        removeYAxisTitle = removeYAxisTitle,
        sortXAxisLabels = sortXAxisLabels,
        reverseXAxisLabels = reverseXAxisLabels,
        plotTheme = plotTheme,
        removeXAxisLine = removeXAxisLine,
        removeYAxisLine = removeYAxisLine,
        showDataLabels = showDataLabels,
        dataLabelType = dataLabelType,
        legendPosition = legendPosition,
        flipOrientation = flipOrientation,
        paddingTop = paddingTop,
        paddingBottom = paddingBottom,
        paddingLeft = paddingLeft,
        paddingRight = paddingRight,
        colorSaturation = colorSaturation,
        dodgeWidth = dodgeWidth,
        pointSize = pointSize,
        pointWhiteBorder = pointWhiteBorder,
        lineWidth = lineWidth,
        useRasterization = useRasterization,
        rasterDPI = rasterDPI,
        setXLimits = setXLimits,
        xMin = xMin,
        xMax = xMax,
        setYLimits = setYLimits,
        yMin = yMin,
        yMax = yMax,
        xAxisTickCount = xAxisTickCount,
        yAxisTickCount = yAxisTickCount,
        fontFamily = fontFamily,
        titleAlignment = titleAlignment,
        captionAlignment = captionAlignment,
        showMedianLine = showMedianLine,
        connectPairedData = connectPairedData,
        highlightSubset = highlightSubset,
        maxDataPoints = maxDataPoints,
        errorBarWidth = errorBarWidth,
        violinScale = violinScale,
        boxplotOutliers = boxplotOutliers,
        boxplotNotch = boxplotNotch,
        addSmoother = addSmoother,
        smootherMethod = smootherMethod,
        smootherSE = smootherSE,
        plotWidth = plotWidth,
        plotHeight = plotHeight,
        useAutoSize = useAutoSize)

    analysis <- tidyplotsClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

