    "ssgBIBase",
    inherit = jmvcore::Analysis,
    public = list(
        initialize = function(options, data=NULL, datasetId="", analysisId="", revision=0) {
            super$initialize(
                package = 'ClinicoPath',
                name = 'ssgBI',
                version = c(1,0,0),
                options = options,
                results = ssgBIResults$new(options=options),
