#' Detect peaks
#'
#'
#' @param data .
#' @param dep .
#' @param group .
#' @param alt .
#' @param varEq .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#' }
#'
#' @export
tsPKS <- function(
    data,
    dep,
    group,
    alt = "notequal",
    varEq = TRUE) {

    if ( ! requireNamespace('jmvcore'))
        stop('tsPKS requires jmvcore to be installed (restart may be required)')

    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(dep), dep, NULL),
            `if`( ! missing(group), group, NULL))

    options <- tsPKSOptions$new(
        dep = dep,
        group = group,
        alt = alt,
        varEq = varEq)

    results <- tsPKSResults$new(
        options = options)

    analysis <- tsPKSClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
